import probe.CallGraph;
import probe.PyCallGraphToProbeConverter;

import java.io.File;
import java.util.ArrayList;

/***
 * Class to convert dot call graph generated by Pycallgraph tool to probe CG.
 */
public class PyCallGraphToProbe {

    /**
     * Method that reads a file from the input path and returns list of probe CGs.
     * @param dotFilePath file path of the dot file
     * @return array list of probe CGs
     */
    public ArrayList<CallGraph> convertToProbe(String dotFilePath){
        PyCallGraphToProbeConverter pyCallGraphToProbeConverter = new PyCallGraphToProbeConverter();
        pyCallGraphToProbeConverter.setReplacementString(new File(dotFilePath).getName());
        return pyCallGraphToProbeConverter.getProbeCG(dotFilePath);
    }
}
